<?phperror_reporting(E_ALL);ini_set('display_errors', 1);require("../../../../connection.php");session_start();    $email    = $_POST['email'];    $password = $_POST['password'];    try {        $query = "SELECT * FROM simple_login WHERE email = :email;";        $stmt = $conn->prepare($query);        $stmt->bindValue(':email', $email);        $stmt->execute();        $user = $stmt->fetch();    } catch (\PDOException $e) {        throw new \PDOException($e->getMessage(), (int) $e->getCode());    }    if ($user['email'] && password_verify($password, $user['password'])) {        $_SESSION['username'] = $user['username'];        echo "Logged in";        header("Location: ../");        exit;    } else {        echo "Wrong login information";        $_SESSION["champ_add"] = "<font color='red'>Wrong login information.</font>";        header("Location: ../");    }    ?>